import React from 'react';
import Style from './Home.module.scss';
import me from '../../img/self.png';
import classNames from 'classnames';
import EmojiBullet from "./EmojiBullet";
import SocialIcon from "./SocialIcon";
import {Box, Button, useTheme } from "@mui/material";
import {info} from "../../info/Info";

export default function Home() {

   return (
      <Box component={'main'} display={'flex'} flexDirection={{xs: 'column', md: 'row'}} alignItems={'center'}
           justifyContent={'center'} minHeight={'calc(100vh - 175px)'}>
         <Box className={classNames(Style.avatar, Style.shadowed)} alt={'image of developer'} style={{background: info.gradient}} component={'img'} src={me} width={{xs: '35vh', md: '40vh'}}
              height={{xs: '35vh', md: '40vh'}}
              borderRadius={'50%'} p={'0.75rem'} mb={{xs: '1rem', sm: 0}} mr={{xs: 0, md: '2rem'}}/>
         <Box>
            <h1>Hi, I'm <span style={{background: info.gradient, WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent'}}>{info.firstName}</span><span className={Style.hand}>ðŸ¤š</span>
            </h1>
            <h2>I'm {info.position}.</h2>
            <Box component={'ul'} p={'0.8rem'}>
               {info.miniBio.map((bio, index) => (
                  <EmojiBullet key={index} emoji={bio.emoji} text={bio.text}/>
               ))}
            </Box>
            <Box display={'flex'} gap={'1.5rem'} justifyContent={'center'} fontSize={{xs: '2rem', md: '2.5rem'}}>
               {info.socials.map((social, index) => (
                  <SocialIcon key={index} link={social.link} icon={social.icon} label={social.label} />
               ))}
               <Button variant="outlined" href="/path/to/resume.pdf" download sx={{ 
                  background: theme.palette.mode === 'dark' ? "#6c63ff" : "#ffffff",
                  color: theme.palette.mode === 'dark' ? "#ffffff" : "#6c63ff",
                  border: "2px solid #6c63ff",
                  borderRadius: "20px",
                  padding: "10px 20px",
                  fontSize: "1.2rem",
                  "&:hover": {
                    background: theme.palette.mode === 'dark' ? "#ffffff" : "#6c63ff",
                    color: theme.palette.mode === 'dark' ? "#6c63ff" : "#ffffff"
                  }
               }}>Download Resume</Button>
            </Box>
         </Box>
      </Box>
   )
}
